import React, { useState } from 'react';
import styled from 'styled-components';

import Button from '../../UI/Button/Button';
import './CourseInput.css';

const FormControl = styled.div`
  margin: 0.5rem 0;

  & label {
    font-weight: bold;
    display: block;
    margin-bottom: 0.5rem;
  }

  & input {
    display: block;
    width: 100%;
    border: 1px solid #ccc;
    font: inherit;
    line-height: 1.5rem;
    padding: 0 0.25rem;
  }

  & input:focus {
    outline: none;
    background: #fad0ec;
    border-color: #8b005d;
  }

  &.invalid{
    border-color: red;
    background-color: #ffd7d7;
  }

  &.invalid label{
    color: red;
  }
`;

const CourseInput = props => {
  const [enteredValue, setEnteredValue] = useState('');
  const [isValid, setIsValid] = useState(true)

  const goalInputChangeHandler = event => {
    // NOTE remove blank space, check if there is an value or more
    if (event.target.value.trim().length > 0) {
      setIsValid(true)
    }
    setEnteredValue(event.target.value);
  };

  const formSubmitHandler = event => {
    event.preventDefault();
    // NOTE remove blank space, check if there is nothing
    if(enteredValue.trim().length === 0) {
      setIsValid(false);
      return;
    }
    props.onAddGoal(enteredValue);
  };

  return (
    <form onSubmit={formSubmitHandler}>

      {/* NOTE Change text color and text box if input is not valid - Dynamic CSS Classes*/}
      <FormControl className={`form-control ${!isValid ? 'invalid' : ''}`}>

      {/* NOTE Change text color and text box if input is not valid*/}
      {/* <div className={`form-control ${!isValid ? 'invalid' : ''}`}> */}

        {/* NOTE Change text color if input is not valid - Dynamic Inline Styles*/}
        <label /* style={{color: !isValid ? 'red' : 'black'}} */>Course Goal</label>

        <input 
          type="text" 
          onChange={goalInputChangeHandler} 

          // NOTE Change text box if input is not valid - Dynamic Inline Styles
          /* style={{
            borderColor: !isValid ? 'red' : '#ccc',
            backgroundColor: !isValid ? 'salmon' : 'transparent'
          }} */

        />
      {/* </div> */}

      </FormControl>

      <Button type="submit">Add Goal</Button>
    </form>
  );
};

export default CourseInput;
